mapper.update:
update customer_house set houseId=#{house.houseId}
	
		,status=#{house.status}
	
	
		,giftAddress=#{house.giftAddress}
	
	
		,callbackTips=#{house.callbackTips}
	
	
		,zoneId=#{house.zoneId}
	
	
		,gift=#{house.gift}
	
	
		,log=#{house.log}
	
	
		,comment=#{house.comment}
	
	
		,hasSoft=#{house.hasSoft}
	
	
		,budgetId=#{house.budgetId}
	
	
		,designType=#{house.designType}
	
	
		,area=#{house.area}
	
	
		,isNew=#{house.isNew}
	
	
		,houseTypeId=#{house.houseTypeId}
	
	
		,houseInfo=#{house.houseInfo}
	
	
		,houseLocation=#{house.houseLocation}
	
where houseId=#{house.houseId}

mapper.select:
select ch.houseId,ch.customerId,ch.createDate, ch.zoneId,z.name name_z,ch.houseInfo,ch.houseLocation,ch.callbackTips, ch.houseTypeId,hs.name name_s,ch.isNew,ch.area,ch.designType,ch.status, ch.budgetId,b.name name_b,ch.hasSoft,ch.comment,ch.log,ch.gift, ch.giftAddress from customer_house ch left join zone z on ch.zoneId=z.zoneId left join house_style hs on hs.styleId=ch.houseTypeId left join budget b on b.budgetId=ch.budgetId where 1=1
and ch.customerId=#{house.customerId}
and ch.houseId=#{house.houseId}

select ch.houseId,ch.customerId,ch.createDate, ch.zoneId,z.name name_z,ch.houseInfo,ch.houseLocation,ch.callbackTips, ch.houseTypeId,hs.name name_s,ch.isNew,ch.area,ch.designType,ch.status, ch.budgetId,b.name name_b,ch.hasSoft,ch.comment,ch.log,ch.gift, ch.giftAddress from customer_house ch left join zone z on ch.zoneId=z.zoneId left join house_style hs on hs.styleId=ch.houseTypeId left join budget b on b.budgetId=ch.budgetId where 1=1
and ch.customerId=#{house.customerId}
order by ch.houseId
